import React, { useState } from "react";

const App2 = () => {

 
let text = "hi?"
 
const color = '#8e44ad';
const [bg,setbg] = useState(color);
  

  const ChangeText = () =>{
   
    let newbg = "#34495e"
    setbg(newbg)
  
  }


  return (
    <>
      <div style={{ justifyContent: "center", display: "flex", textAlign: "center" , backgroundColor: color  }}>
        <button style={{ width: "100px", margin: "auto", backgroundColor: "blue", color: "#fff",
         border: "none", height: "50px", fontSize: "2rem" }} onClick={ChangeText}>
          {text}</button>
      </div>
    </>
  )
}

export default App2;
