import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

const Compa = () =>{
    const [num,Upnum] = useState();
    const [names,Upnames] = useState();
    const [moves,Upmoves] = useState();
    const InputE = (event) =>{
        Upnum(event.target.value)
    }

    useEffect(()=>{
        
       const getData = async () =>{
           const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${num}`)
           console.log(res)
           Upnames(res.data.name);
           Upmoves(res.data.moves.length)
           
       }
        getData();
    })
    return(
        <>
        <h1>The name of pokemon is {names}</h1>
        <h1>The number of moves are {moves}</h1>
        <h1>You choose {num} value</h1>
        <select value={num} onChange={InputE}>
            <option value="1">1</option>
            <option value="25">25</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
        </select>
        </>
    )
}

export default Compa;
