import React, { useState } from "react";

const App2 = () => {

 
let text = "hi?"
  const[ini , fin]    = useState(text);
   
 
  

  const ChangeText = () =>{
    text = 'ohho!'
    fin(text);
  }


  return (
    <>
      <div style={{ justifyContent: "center", display: "flex", textAlign: "center" }}>
        <button style={{ width: "100px", margin: "auto", backgroundColor: "blue", color: "#fff",
         border: "none", height: "50px", fontSize: "2rem" }} onClick={ChangeText()}>
          {text}</button>
      </div>
    </>
  )
}

export default App2;
