//     fontFamily: "Montserrat",
//     fontWeight: "bold",
//     cursor: "pointer"
//   }
// }))

const Header = () => {

  const [curr , setCurr ] = useState();
  

  const InputEv = (e) =>{
          setCurr(e.target.value)
  }

  const exp = curr;
  

  
  
  return (
    <>

    <div className="header">
      <div className="box1">
         <p>Crypto Hunt</p>
      </div>

      <div className="box2">
        <select value={curr} onChange={InputEv}>
          <option value="INR">INR</option>
          <option value="USD">USD</option>
        </select>
      </div>

    </div>
      
    </>
  )
}

export default Header;
