import React, { useState } from "react"

const App2 = () =>{

 const OnSubmit = (event) =>{
     event.preventDefault();
     alert('from-submitted')
 }

  const[text,Uptext]  = useState;
  const[text2,Uptext2]  = useState;
  return(
    <>
    <h1>Hello</h1>
    <form >
      <input type="text" placeholder="enter your first name" value={text}  />
      <input type="text" placeholder="enter your last name"  value={text2} />
      <button onClick={OnSubmit}> Click Me</button>
    </form>
    </>
  )
}

export default App2;
