import React, { useState } from 'react';

const App2 = () =>{

  const[text,Uptext]   = useState({
    fname: "",
    lname: "",
    email: ""
  })

  const InputEvent = (event) =>{
       const name = event.target.name;
       const value = event.target.value;

     Uptext((prev) =>{
       if(name=="fname"){
         return{
           fname: value,
           lname: prev.lname,
           email: prev.email,
         };
       }

      else if(name=="lname"){
        return{
          fname: prev.fname,
          lname: value,
          email: prev.email,
        };
      }

     else if(name=="email"){
        return{
          fname: prev.fname,
          lname: prev.lname,
          email: value,
        };
      }

       
     })
  }

  const OnSubmit = (event) =>{
      event.preventDefault();
      alert('form submitted')
  }
  return(
    <>
    <h1>Hello  {text.fname} {text.lname} {text.email} </h1>
    <form>
    <input type="text" placeholder='enter your first name' name='fname' onChange={InputEvent} value = {text.fname} />
    <input type="text" placeholder='enter your last name' name='lname' onChange={InputEvent} value = {text.lname} />
    <input type="text" placeholder='enter your email' name='email' onChange={InputEvent} value = {text.email} />
    <button onClick={OnSubmit}>Click me</button>
    </form>
    </>
  )
}

export default App2;
