import React from "react";
import { useState } from "react";
import './create.css';

const Create = () =>{

    const[notes,Setnotes] = useState({
        title: '',
        content: ''
    })

    
 return(
   <>
   <div className="create_div">
       <form>
           <input type="text" placeholder="Title" onChange={InputEvent} value={notes.title}  />
           <textarea  cols="" rows="" placeholder="add your reminder here"  onChange={InputEvent} value={notes.content}  ></textarea>
           <button className="btn"> + </button>
       </form>
   </div>
   </>
 )
}

export default Create;
