import React from "react";
import ReactDOM from "react-dom";

let greet = new Date();
greet = greet.getHours;

let wish = '';

if(greet>=1 && greet<12){
  wish = 'Good Morning'
}

else if(greet>=12 && greet<4){
  wish = 'Good AfterNoon'
}

else{
  wish = 'Good Night'
}

console.log(greet);

ReactDOM.render(
  <>
  hello sir , {wish}
  </> , document.getElementById('root')
)
