import React from "react";
import Header from "./Header";
import Create from "./Create"
import Note from "./Note"
import { useState } from "react";

const App2 = () => {

  const [text,Uptext] = useState([]);

const addNote = (notes) =>{
    Uptext((prev)=>{
      return[...prev,notes]
          
      
    })
}

const OnDelete = (id) =>{
  Uptext((prev)=>{
    prev.filter((curr,index)=>{
          return index!=id
    })
  })
}

  return (
    <>

      <Header />
      <Create 
      passNote = {addNote}
       />
      {text.map((val,index)=>{
         return <Note
               key = {index}
               id = {index}
               title = {val.title}
               content = {val.content}
               deleteitem = {OnDelete}
         />
      })}
    </>
  )
}

export default App2;
