import React, { useState } from "react"

const App2 = () =>{

 const OnSubmit = (event) =>{
     event.preventDefault();
     alert('from-submitted')
 }

  const[text,Uptext]  = useState('')
  const[text2,Uptext2]  = useState('')

  const InputEvent1 = (events) =>{
     let newT = events.target.value;
     Uptext(newT)
  }

  const InputEvent2 = (events) =>{
    let newT = events.target.value;
    Uptext2(newT);
 }


  return(
    <>
    <h1>Hello {text} {text2}</h1>
    <form >
      <input type="text" placeholder="enter your first name" onChange={InputEvent1} value={text}  />
      <input type="text" placeholder="enter your last name" onChange={InputEvent2}  value={text2} />
      <button onClick={OnSubmit}> Click Me</button>
    </form>
    </>
  )
}

export default App2;
