import React, { useState } from "react";

const App2 = () =>{

     const[text,Uptext] = useState(0);

     const Incre = () =>{
       let newNum = text + 1;
       Uptext(newNum);
     }

     const Decre = () =>{
       if(text === 0){
         alert('ab zero se kam ni hoga...tmhari tarah')
       }

       else if(text > 0){
         let newNum2 = text - 1;
         Uptext(newNum2);
       }
     }
   return(
     <>
     <h1>{text}</h1>
     <button onClick={Incre}>increase</button>
     <button onClick={Decre}>decrease</button>
     </>
   )
}

export default App2;
