import React, { useState } from 'react';

const App2 = () =>{

  const[text,Uptext]   = useState({
    fname: "",
    lname: "",
    email: ""
  })

  const OnSubmit = (event) =>{
      event.preventDefault();
      alert('form submitted')
  }
  return(
    <>
    <h1>Hello</h1>
    <form>
    <input type="text" placeholder='enter your first name' name='fname' onChange={InputEvent} value = {text.fname} />
    <input type="text" placeholder='enter your last name' name='lname' onChange={InputEvent} value = {text.lname} />
    <input type="text" placeholder='enter your email' name='email' onChange={InputEvent} value = {text.email} />
    <button onClick={OnSubmit}>Click me</button>
    </form>
    </>
  )
}

export default App2;
