import React from "react";
import { useState } from "react";
import Create from "./Create";

import Header from "./Header";
import Note from "./Note";

const App2 = () =>{
  const [item,Setitem] = useState([])
const addNote = (note) =>{
  // alert('I am clicked')
  Setitem((prev) =>{
    return [...prev , note]
  });
  
}

 return(
   <>
   <Header/>
   <Create 
   passNote = {addNote}
   />
   <Note />
   {item.map((val,index) =>{
     return <Note
           key = {index}
           id = {index}
           title = {val.title}
           content = {val.content}
     />

     
   })
   }
   
   </>
 )
}

export default App2;
