import React from "react";
import { useState } from "react";

const Create = (props) =>{

    const[note , Setnote] = useState({
        title: '',
        content: ''
    });

    const InputEvent = (event) =>{

        // const value = event.target.value;
        // const name = event.target.name;

        const {name,value}  = event.target

        Setnote((prev)=>{
           return{
               ...prev ,
               [name] : value,
           }
        })
    }

    const addNotes = () =>{
       props.passNote;
    }


 return(
   <>
   <div className="main_note">
       <form >
           <input type="text" placeholder="Title" autoComplete="off" name="title" onChange={InputEvent} value = {note.title} />
           <textarea cols="" rows="" placeholder="write a note..." name="content" onChange={InputEvent} value={note.content}></textarea>
           <button onClick={addNotes}>
               +
           </button>
       </form>
   </div>
   </>
 )
}

export default Create;
