import React, { useState } from 'react'

const App2 = () =>{

 const [text , uptext] = useState({
   fname: "",
   lname: "",
   email: ""
 });
  
  
const inputEvent = (event) =>{
  const value = event.target.value;
  const name = event.target.name;

  uptext((prev) =>{
        if(name === "fname"){
          return{
            fname: value,
          lname : prev.lname,
          email: prev.email
          }
        }

        else  if(name === "lname"){
          return{
            fname: prev.fname,
          lname : value,
          email : prev.email,
          }
        }

        else  if(name === "email"){
          return{
            fname: prev.fname,
          lname : prev.lname,
          email:  value,
          }
        }
  })
  
}


const OnSubmit = (event) =>{
        event.preventDefault()
        alert('form submitted')
        
}

  return (
    <>
    <div>
      <form onSubmit={OnSubmit}>
      <h1> Hello {text.fname} {text.lname} {text.email} </h1>
      <input type="text"  placeholder='enter your first name' name='fname'  onChange={inputEvent} value={text.fname}/>
      <input type="text"  placeholder='enter your last name'  name='lname' onChange={inputEvent} value={text.lname}/>
      <input type="text"  placeholder='enter your email'  name='email' onChange={inputEvent} value={text.email}/>
      <button  onClick={OnSubmit}> Click Me </button>
      </form>
    </div>
    </>
  );
}

export default App2;
