import { Input, LinearProgress, TableContainer, TextField } from '@mui/material';
import { makeStyles } from '@mui/styles';
import { createTheme } from '@mui/system';
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { ThemeProvider } from 'styled-components';
import "./CoinTable.css"
import { CoinList } from './config/Api';

const CoinTable = () => {

  const [coins, Setcoins] = useState([]);
  const [loading, setloading] = useState(false);
  const[search , Setsearch] = useState();
  const[text , Settext] = useState();

  const InputE = (e) =>{
      Settext(e.target.value)
  }

 

  const fetchCoins = async () => {
    setloading(true);
    const res = await axios.get(CoinList("USD"));
    setloading(false);
    Setcoins(res.data);


  }

  useEffect(() => {
    fetchCoins();
  }, [])

  

  const darkTheme = createTheme(({
    palette: {
      main: "#fff"
    },
    type: "dark",
  }));

  // const useStyles = makeStyles(()=>({
  //   textField:{
  //     border: '1px solid white'
  //   }
  // }))

  // const classes = useStyles();

  return (
    <ThemeProvider theme={darkTheme}>
      <div className='table' style={{ textAlign: "center" }}>
        <h4>
          Cryotocurrency prices by market Cap
        </h4>
        <input type="text" id="input" placeholder='search crypto currency here...' value={text} onChange = {InputE} />
        <TableContainer>
         {loading ? (
           <LinearProgress style={{backgroundColor: "gold"}}/>
         ) : (
          <>

          </>
         )}

         
        </TableContainer>
      </div>
    </ThemeProvider>
  )
}

export default CoinTable;
