import { Input, LinearProgress, Table, TableCell, TableContainer, TableHead, TableRow, TextField } from '@mui/material';
import { makeStyles } from '@mui/styles';
import { createTheme } from '@mui/system';
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { ThemeProvider } from 'styled-components';
import "./CoinTable.css"
import { CoinList } from './config/Api';

const CoinTable = () => {

  const [coins, Setcoins] = useState([]);
  const [loading, setloading] = useState(false);
  const [search, Setsearch] = useState();
  const [text, Settext] = useState();

  const InputE = (e) => {
    Settext(e.target.value)
  }



  const fetchCoins = async () => {
    setloading(true);
    const res = await axios.get(CoinList("USD"));
    setloading(false);
    Setcoins(res.data);


  }

  useEffect(() => {
    fetchCoins();
  }, [])



  const darkTheme = createTheme(({
    palette: {
      main: "#fff"
    },
    type: "dark",
  }));

  // const useStyles = makeStyles(()=>({
  //   textField:{
  //     border: '1px solid white'
  //   }
  // }))

  // const classes = useStyles();

  return (
    <ThemeProvider theme={darkTheme}>
      <div className='table' style={{ textAlign: "center" }}>
        <h4>
          Cryotocurrency prices by market Cap
        </h4>
        <input type="text" id="input" placeholder='search crypto currency here...' value={text} onChange={InputE} />
        <TableContainer height="200">
          {loading ? (
            <LinearProgress style={{ backgroundColor: "gold" }} />
          ) : (
            <>
              {/* <Table>
            <TableHead style={{backgroundColor: "yellow" ,height: "200px"}}>
             <TableRow>
               {["coin" , "price" , "24h Change" , "Market Cap"].map((head)=>{
                 <TableCell
                 style={{
                   color: "black",
                   fontWeight: "700",
                   fontFamily: "Monteserrat"

                 }}
                 key = {head}
                 align = {head === "coin" ? "" : "right"}
                 >

                 </TableCell>
               })}
             </TableRow>
            </TableHead>

          </Table> */}
              <table class="table">
                <thead>
                  <tr>
                    <th scope="col">#</th>
                    <th scope="col">First</th>
                    <th scope="col">Last</th>
                    <th scope="col">Handle</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <th scope="row">1</th>
                    <td>Mark</td>
                    <td>Otto</td>
                    <td>@mdo</td>
                  </tr>
                  <tr>
                    <th scope="row">2</th>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>@fat</td>
                  </tr>
                  <tr>
                    <th scope="row">3</th>
                    <td colspan="2">Larry the Bird</td>
                    <td>@twitter</td>
                  </tr>
                </tbody>
              </table>
            </>
          )}


        </TableContainer>
      </div>
    </ThemeProvider>
  )
}

export default CoinTable;
