import React, { useState } from "react";

const App2 = () => {


  const [text, Uptext] = useState('')
  const [test, Uptest] = useState([])

  const InputEvent = (event) => {
    let newT = event.target.value;
    Uptext(newT);
  }

  const OnSubmit = (events) => {
    events.preventDefault();
    Uptest((prev) => {
      return [...prev, text]
    });
    Uptext('')
  }

  return (
    <>
      <div className="main_div">
        <div className="center_div">
          <br />
          <h1> ToDo list</h1>
          <br />
          <input type="text" placeholder="add Items" onChange={InputEvent} value={text} />
          <button onClick={OnSubmit}> + </button>


          <ol>
            {/* <li> {test} </li> */}

            {test.map((itemVal) => {
              return (
                <>
                  <li>{itemVal}</li>
                </>
              )
            })}
          </ol>
        </div>
      </div>
    </>
  )

}

export default App2;
