import React, { useState } from "react";

const App2 = () => {

 

 
//  const color = '#8e44ad';
const [bg,setbg] = useState('#8e44ad');

const[text,newText] = useState('hi');

  

  const ChangeText = () =>{
   
    let newbg = "#34495e"
    setbg(newbg);
    
    let upText = 'Ohoo!';
    newText(upText)
  
  }

  const newFunc = () =>{
    newbg = "#000";
    setbg(newbg);

    upText = "fuck!";
    newText(upText)
  }


  return (
    <>
      <div style={{ justifyContent: "center", display: "flex", textAlign: "center" , backgroundColor: bg  }}>
        <button style={{ width: "100px", margin: "auto", backgroundColor: "blue", color: "#fff",
         border: "none", height: "50px", fontSize: "2rem" }} onClick={ChangeText} onDoubleClick={newFunc}>
          {text}</button>
      </div>
    </>
  )
}

export default App2;
