import React from "react";
import './Note.css'

const Note = (props) => {

    const DeleteI = () =>{
        props.deleteitem(props.id);
    }
    return (
        <>
            <div className="note_div">
                <h1>{props.title}</h1>
                <p>{props.content}</p>
                <button className="btn2" onClick={DeleteI}> - </button>
            </div>
        </>
    )
}

export default Note;
