import React from "react";
import { useState } from "react";
import './create.css';

const Create = (props) => {

    const [notes, Setnotes] = useState({
        title: '',
        content: '',
    })


    const InputEvent = (event) => {

        const name = event.target.name;
        const value = event.target.value;
        Setnotes((prev) => {
            return {

                ...prev,
                [name]: value

            }
        })
    }

    const Clicked = (event) => {
        event.preventDefault();
        props.passNote(notes);
        Setnotes({
            title: '',
            content: '',
        })
    }


    return (
        <>
            <div className="create_div">
                <form>
                    <input type="text" placeholder="Title" name="title" onChange={InputEvent} value={notes.title} autoComplete="off" />
                    <textarea cols="" rows="" placeholder="add your reminder here" name="content" onChange={InputEvent} value={notes.content}  ></textarea>
                    <button className="btn" onClick={Clicked}> + </button>
                </form>
            </div>
        </>
    )
}

export default Create;
